  //GET BY ID
  public BaseResponse<MODELQuanLyChungTu> GetById(GetByIdRequest request)
  {
      var response = new BaseResponse<MODELQuanLyChungTu>();
      try
      {
          var result = new MODELQuanLyChungTu();
          var data = _unitOfWork.GetRepository<ENTITIES.DBContent.DM_QUANLYCHUNGTU>()
              .Find(x => x.Id == request.Id && !x.isDeleted);
          if (data is null)
          {
              throw new Exception("Kh√¥ng t√¨m th·∫•y th√¥ng tin");
          }
          else
          {
              result = _mapper.Map<MODELQuanLyChungTu>(data);
              result.ListTepDinhKemChungTu = GetListTepDinhKem(result.Id);
          }
          response.Data = result;
      }
      catch (Exception ex)
      {
          response.Error = true;
          response.Message = ex.Message;
      }

      return response;
  }

  //GET BY POST (INSERT/UPDATE)
  public BaseResponse<PostQuanLyChungTuRequest> GetByPost(GetByIdRequest request)
  {
      var response = new BaseResponse<PostQuanLyChungTuRequest>();
      try
      {
          var result = new PostQuanLyChungTuRequest();
          var data = _unitOfWork.GetRepository<ENTITIES.DBContent.DM_QUANLYCHUNGTU>().Find(x => x.Id == request.Id);
          if (data is null)
          {
              result.Id = Guid.NewGuid();
              result.IsEdit = false;
          }
          else
          {
              result = _mapper.Map<PostQuanLyChungTuRequest>(data);
              result.IsEdit = true;
              result.ListTepDinhKem = GetListTepDinhKem(result.Id);
              //result.
          }
          response.Data = result;
      }
      catch (Exception ex)
      {
          response.Error = true;
          response.Message = ex.Message;
      }

      return response;
  }
    private List<MODELTepDinhKem> GetListTepDinhKem(Guid Id)
    {
        var result = _unitOfWork.GetRepository<ENTITIES.DBContent.DM_QUANLYCHUNGTU_TEPDINHKEM>().GetAll(x => x.LienKetChungTuId == Id).ToList();
        var mappedResult = _mapper.Map<List<MODELTepDinhKem>>(result);
        mappedResult.ForEach(x =>
        {
            x.TenTapTinFull = x.TenFile + x.TenMoRong;
        });
        return mappedResult;
    }

